class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let a=parseInt($(e).attr("data-likes"));1==t.data.deleted?(a-=1,$(e).attr("data-likes",a),$(e).html(` <i class="far fa-heart"></i> ${a}`)):(a+=1,$(e).attr("data-likes",a),$(e).html(` <i class="fas fa-heart"></i> ${a}`))})).fail((function(t){console.log("error in completing the request")}))}))}}